	public function index(&$page, $canonical, $home_url, $permalink_structure)
	{
		$page = $this->update_content($page, $home_url, $permalink_structure);

		// The attribute 'href' in tag 'link rel=stylesheet for fonts' is set to the invalid value...
		$page = preg_replace('#^<link rel=\'stylesheet\' id=\'twentysixteen-style-css\' .+ />$#im', '', $page, 1);

		$page = $this->update_header($page, $canonical);

		return $page;
	}
